using Microsoft.EntityFrameworkCore;using AmazBlog.Share;namespace AmazBlog.Infrastructure;public class AmazBlogContext : DbContext{    public DbSet<Posts> Posts { get; set; }    public DbSet<Options> Options { get; set; }    public string DbPath { get; }    public AmazBlogContext()    {        //var folder = Environment.SpecialFolder.LocalApplicationData;                                                                                var path = "/Users/yanxl/Code/amazBlog/Infrastructure";        DbPath = System.IO.Path.Join(path, "blogging.db");    }    protected override void OnConfiguring(DbContextOptionsBuilder options)        => options.UseSqlite($"Data Source={DbPath}");}